// const defaultState = {
//     count: 0
// }

const [defaultState, setState] = useState(0);

const Reducer = (state = defaultState, action) => {
    switch (action.type) {
      case "counter-increment": {
        return {
          ...state, 
          count: state.count + action.payload
        }
      }
      case "counter-decrement": {
        return {
          ...state, 
          count: state.count - action.payload
        }
      }

      case "counter-reset": {
        return {
            ...state,
            count: state.count.setState(0)
        }
      }

      default: return state;
    }
}

export default Reducer;
