function Reducers () {
    const store = createStore(reducer);

    const defaultState = {
        count: 0
    }
    const reducer = (state = defaultState, action) => {
        switch (action.type) {
          case "counter-increment": {
            return {
              ...state, 
              count: state.count + action.payload
            }
          }
          case "counter-decrement": {
            return {
              ...state, 
              count: state.count - action.payload
            }
          }

          default: return state;
        }
    }
}

  export default Reducers;
