const defaultState = {
    count: 0
}

const Reducer = (state = defaultState, action) => {
    switch (action.type) {
      case "COUNTER_INCREMENT": {
        return {
          ...state, 
          count: state.count + action.payload
        }
      }
      case "COUNTER_DECREMENT": {
        return {
          ...state, 
          count: state.count - action.payload
        }
      }

      case "counter-reset": {
        return {
            ...state,
            count: state.count = action.payload
        }
      }

      default: return state;
    }
}

export default Reducer;
